import React from 'react';
import TempoContainer from './TempoContainer';
import SaveContainer from './SaveContainer';

export default class DisplayContainer extends React.Component {
  state = {
    status: "WELCOME TO DRUMBLR"
  }

  setTimeout = (() => {
    // after 1000ms, call the `setTimeout` callback
    // In the meantime, continue executing code below
    setTimeout = (() => {
      this.resetStatus() //runs second after 1100ms
    },3000)
    this.updateStatus() //runs first, after 1000ms
  },100);

  updateStatus = () => {
    this.setState({
      status: this.props.handleStatusUpdate
    })
  }

  resetStatus = () => {
    this.setState({
      status: "WELCOME TO DRUMBLR"
    })
  }

  render() {

    return (
      <div className="DisplayContainer">
        <div className="LCD">
          <div className="Status">{this.state.status}</div>

          <TempoContainer bpm={this.props.bpm} 
            adjustBPM={this.props.adjustBPM}
          />

          <SaveContainer handleSave={this.props.handleSave}
            handleOnChange={this.props.handleOnChange}
            value={this.props.value}
          />

        </div>
      </div>
    )
  }

}
