import React, { Component } from 'react';
import Button from './Button';

export class SampleRow extends Component {

  getDrumObjButtons = () => {
    let buttonRow = []
    this.props.drumObj.isPlaying.map((stepBoolean, index) => {
        buttonRow.push(<Button key={this.props.drumObj.name + index} id={this.props.drumObj.midiID} sampleIndex={this.props.drumIndex} beatIndex={index} togglePlaying={this.props.togglePlaying} sampleObj={this.props.drumObj} sequenceThisNote={this.props.sequenceThisNote}/>)
      })
    return buttonRow
    }

  render() {
    console.log("Sample Row", this.props.appProps)
    return (
      <div className="buttonRowContainer">
        	<Button className="inactiveStep" defaultChecked={this.props.appProps.tracks[0][0]} onClick={(e)=>this.props.appProps.toggleDrum(0,0)} />
					<Button className="inactiveStep" defaultChecked={this.props.appProps.tracks[0][1]} onClick={(e)=>this.props.appProps.toggleDrum(0,1)} />
					<Button className="inactiveStep" defaultChecked={this.props.appProps.tracks[0][2]} onClick={(e)=>this.props.appProps.toggleDrum(0,2)} />
					<Button className="inactiveStep" defaultChecked={this.props.appProps.tracks[0][3]} onClick={(e)=>this.props.appProps.toggleDrum(0,3)} />
					<Button className="inactiveStep" defaultChecked={this.props.appProps.tracks[0][4]} onClick={(e)=>this.props.appProps.toggleDrum(0,4)} />
					<Button className="inactiveStep" defaultChecked={this.props.appProps.tracks[0][5]} onClick={(e)=>this.props.appProps.toggleDrum(0,5)} />
					<Button className="inactiveStep" defaultChecked={this.props.appProps.tracks[0][6]} onClick={(e)=>this.props.appProps.toggleDrum(0,6)} />
					<Button className="inactiveStep" defaultChecked={this.props.appProps.tracks[0][7]} onClick={(e)=>this.props.appProps.toggleDrum(0,7)} />
					<Button className="inactiveStep" defaultChecked={this.props.appProps.tracks[0][8]} onClick={(e)=>this.props.appProps.toggleDrum(0,8)} />
					<Button className="inactiveStep" defaultChecked={this.props.appProps.tracks[0][9]} onClick={(e)=>this.props.appProps.toggleDrum(0,9)} />
					<Button className="inactiveStep" defaultChecked={this.props.appProps.tracks[0][10]} onClick={(e)=>this.props.appProps.toggleDrum(0,10)} />
					<Button className="inactiveStep" defaultChecked={this.props.appProps.tracks[0][11]} onClick={(e)=>this.props.appProps.toggleDrum(0,11)} />
					<Button className="inactiveStep" defaultChecked={this.props.appProps.tracks[0][12]} onClick={(e)=>this.props.appProps.toggleDrum(0,12)} />
					<Button className="inactiveStep" defaultChecked={this.props.appProps.tracks[0][13]} onClick={(e)=>this.props.appProps.toggleDrum(0,13)} />
					<Button className="inactiveStep" defaultChecked={this.props.appProps.tracks[0][14]} onClick={(e)=>this.props.appProps.toggleDrum(0,14)} />
					<Button className="inactiveStep" defaultChecked={this.props.appProps.tracks[0][15]} onClick={(e)=>this.props.appProps.toggleDrum(0,15)} />
      </div>
    )
  }
}

export default SampleRow
