import React from 'react'

export default class PlayPauseBtn extends React.Component {

  state = {
    playActive: false,
    stopActive: false
}

handlePlay = () => {  
  this.setState({
      playActive: !this.state.playActive
  });
}

handleStop = () => {  
  this.setState({
      stopActive: !this.state.stopActive
  });
}

  render() {
    return (
      <>
        <div className="PlayBtn" onClick={this.props.playLoop}>
          <p>▶</p>
        </div>
        <div className="StopBtn" onClick={this.props.stopLoop}>
          <p>◼️</p>
        </div>
      </>
    )
  }

}
